"use strict";function Counter(a){this.targetDate=new Date(a).getTime()}function addZero(a){return a>9?a:"0"+a}Counter.prototype.countdown=function(a){var b,c,d,e,f=document.getElementById(a),g=this.targetDate;setInterval(function(){var a=(new Date).getTime(),h=(g-a)/1e3;b=parseInt(h/86400),h%=86400,c=parseInt(h/3600),h%=3600,d=parseInt(h/60),e=parseInt(h%60),f.innerHTML=b+"d "+addZero(c)+"h "+addZero(d)+"m "+addZero(e)+"s"},1e3)},function(){var a=new Counter("10 Sep 2016 13:00");a.countdown("time")}();